import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    backgroundColor: theme.palette.error.main
  },
  imgFluid: {
    maxWidth: "100%",
    height: "auto",
    paddingRight: theme.spacing(2),
    marginBottom: theme.spacing(1),
  }
}));

export default useStyles
