    // margin: theme.spacing(2, 0, 2),
    padding: '0px',
    // backgroundColor: theme.palette.error.main
  },
  imgFluid: {
    maxWidth: "100%",
    height: "auto",
    paddingRight: theme.spacing(2),
    marginBottom: theme.spacing(1.7),
  }
