import React from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import { env } from './config';

export default class Form extends React.Component {
    state = {
        formData: {
            name: '',
            email: '',
            message: ''
        },
        submitted: false,
        formEmailSent: false
        // feedback: '',
        // formSubmitted: false,
    }

    static sender = 'sender@example.com';

    handleChange = (event) => {
        const { formData } = this.state;
        formData[event.target.name] = event.target.value;
        this.setState({ formData });
    }

    handleSubmit = (event) => {
      event.preventDefault();

      const {
        REACT_APP_EMAILJS_RECEIVER: receiverEmail,
        REACT_APP_EMAILJS_TEMPLATEID: template,
        REACT_APP_EMAILJS_USERID: user,
      } = env;

      this.sendFeedback(
        template,
        this.sender,
        receiverEmail,
        this.state.feedback,
        user
      );

      this.setState({ submitted: true }, () => {
        setTimeout(() => this.setState({ submitted: false }), 5000);
      });
    }

    // Note: this is using default_service, which will map to whatever
    // default email provider you've set in your EmailJS account.
    sendFeedback = (templateId, senderEmail, receiverEmail, feedback, user) => {
      window.emailjs
      .send('default_service', templateId, {
          senderEmail,
          receiverEmail,
          feedback
        },
        user
      )
      .then(res => {
        this.setState({
          formEmailSent: true
        });
      })
      // Handle errors here however you like
      .catch(err => console.error('Failed to send feedback. Error: ', err));
    }


    handleCancel() {
      this.setState({
        formData: {
          name: '',
          email: '',
          message: ''
        }
      });
    }

    render() {
        const { formData, submitted } = this.state;
        return (
            <ValidatorForm
              ref="form"
              onSubmit={this.handleSubmit}
            >
              <Grid container item xs={12} sm={8} direction="row">
              <Grid item xs={12} sm={5} style={{ marginBottom: 15, marginRight: 15 }}>
                <TextValidator
                  fullWidth
                  label="Name"
                  onChange={this.handleChange}
                  name="name"
                  variant="outlined"
                  value={formData.name}
                  validators={['required']}
                  errorMessages={['Required']}
                />
              </Grid>
              <Grid item xs={12} sm={5} style={{ marginBottom: 15 }}>
                <TextValidator
                  fullWidth
                  label="Email"
                  onChange={this.handleChange}
                  name="email"
                  variant="outlined"
                  value={formData.email}
                  validators={['required', 'isEmail']}
                  errorMessages={['Required', 'Email is not valid']}
                />
              </Grid>
              <TextValidator
                fullWidth
                label="Message"
                onChange={this.handleChange}
                multiline
                rows={5}
                variant="outlined"
                name="message"
                value={formData.message}
                validators={['required']}
                errorMessages={['Required']}
                style={{ marginBottom: 15 }}
              />
              <Button
                color="primary"
                variant="contained"
                type="submit"
                disabled={submitted}
              >
              {(submitted && 'Sent!') || (!submitted && 'Send')}
              </Button>
              </Grid>
            </ValidatorForm>
        );
    }
}
