import { StyleSheet, Font } from '@react-pdf/renderer'
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  pdfViewer: {
    height: '650px',
    marginTop: theme.spacing(2)
  },
  button: {
    // borderLeft: `1px solid ${theme.palette.divider}`,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: 0,
    width: '200px',
    textTransform: 'capitalize',
    backgroundColor: 'transparent'
  },
  pdfDownloadLink: {
    textDecoration: 'none'
  }
}));

const documentStyles = StyleSheet.create({
  body: {
    flexDirection: 'row',
    padding: 45,
    lineHeight: 1.3
  },
  title: {
    fontSize: 24,
    textAlign: 'center',
    fontFamily: 'Helvetica'
  },
  author: {
    fontSize: 12,
    textAlign: 'center',
    marginBottom: 40,
  },
  subtitle: {
    fontSize: 18,
    margin: 12,
    fontFamily: 'Helvetica'
  },
  text: {
    margin: 12,
    fontSize: 14,
    textAlign: 'justify',
    fontFamily: 'Helvetica'
  },
  image: {
    marginVertical: 15,
    marginHorizontal: 100,
  },
  header: {
    fontSize: 12,
    marginBottom: 20,
    textAlign: 'center',
    color: 'grey',
  },
  pageNumber: {
    position: 'absolute',
    fontSize: 12,
    bottom: 30,
    left: 0,
    right: 0,
    textAlign: 'center',
    color: 'grey',
  },
  leftColumn: {
    width: '40%',
    height: '100%',
    flexDirection: 'column'
  },
  leftTopSection: {
    flexDirection: 'column',
    width: '100%',
    height: 100
  },
  rightColumn: {
    flexDirection: 'column',
    width: '60%',
    height: '100%'
  },
  rightTopSection: {
    flexDirection: 'column',
    width: '100%',
    height: 100
  },
  nameHeading: {

  },
  descriptionHeading: {
    fontFamily: 'Helvetica',
    marginTop: 16,
    marginBottom: 10
  },
  description: {
    fontSize: 12,
    fontFamily: 'Helvetica',
    marginBottom: 20
  },
  descriptionRole: {
    fontSize: 12,
    fontFamily: 'Helvetica'
  },
  descriptionYear: {
    fontSize: 12,
    fontFamily: 'Helvetica',
    color: 'grey'
  }
});

